import { createSlice, PayloadAction } from '@reduxjs/toolkit';

interface loginStateType {
  init: number;
}

const initialState = {
  login: 1,
};

const StartReducer = createSlice({
  name: 'StartReducer',
  initialState,
  reducers: {
    setLogin(state, action: PayloadAction<number>) {
      state.login = action.payload;
    },
  },
});

export default StartReducer.reducer;
export const { setLogin } = StartReducer.actions;
