import * as React from 'react';
import { useState, useEffect } from 'react';

import { useNavigate } from 'react-router-dom';
import { useTypedSelector } from '../../hooks/useTypedSelector';
import BookCover from './BookCover';
import styles from './style.module.css';

import chineseHP from './chineseHP.jpeg';

function First() {
  const navigate = useNavigate();
  const [pariState, setPariState] = useState();
  const [activeBook, setActiveBook] = useState();
  const { bookHeader } = useTypedSelector((state) => state.PariState);

  interface BookCoverProps {
    image: string;
    header: string;
  }

  const booksArray: BookCoverProps[] = [
    { image: chineseHP, header: 'harry potter' },
    { image: chineseHP, header: 'harry potter' },
    { image: chineseHP, header: 'harry potter' },
    { image: chineseHP, header: 'harry potter' },
    { image: chineseHP, header: 'harry potter' },
    { image: chineseHP, header: 'harry potter' },
    { image: chineseHP, header: 'harry potter' },
    { image: chineseHP, header: 'harry potter' },
    { image: chineseHP, header: 'harry potter' },
  ];

  return (
    <div className={styles.container}>
      <button onClick={() => navigate(-1)}>back</button>
      <h1>choose a book</h1>
      <ul>
        {booksArray.map((item, index) => (
          <BookCover image={item.image} header={item.header} active={bookHeader == item.header} />
        ))}
      </ul>
      <button onClick={() => navigate('/second')}>next</button>
    </div>
  );
}

export default First;
