import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useTypedSelector } from '../../hooks/useTypedSelector';
import { useAction } from '../../hooks/useAction';
import styles from './style.module.css';
import Counter from '../../Components/Counter';

function Second() {
  const navigate = useNavigate();

  const { bookHeader, bookCover, countHours } = useTypedSelector((state) => state.PariState);
  const { setBookHeader, setBookCover, setCountHours } = useAction();

  return (
    <div className={styles.container}>
      <button onClick={() => navigate(-1)}>back</button>
      <div className={styles.bookContainer}>
        <img src={bookCover} />
        <div className={styles.aboutBook}>{bookHeader}</div>
      </div>
      <div className="question">
        <p>how long will you read this book?</p>
        <Counter count={countHours} setCount={setCountHours} />
      </div>

      <button onClick={() => navigate('/third')}>next</button>
    </div>
  );
}

export default Second;
