import * as React from 'react';
import { useState, useEffect } from 'react';

import { useNavigate } from 'react-router-dom';
import { useTypedSelector } from '../../hooks/useTypedSelector';
import BookCover from './BookCover';
import styles from './style.module.css';

import c21 from './c21.jpg';
import algor from './algor.jpg';
import chinese from './chinese.jpg';
import gypnos from './gypnos.jpg';
import HP from './HP.jpg';
import kant from './kant.jpg';
import kung from './kung.jpg';
import landau from './landau.jpg';
import think from './think.jpg';

function First() {
  const navigate = useNavigate();
  const [pariState, setPariState] = useState();
  const [activeBook, setActiveBook] = useState();
  const { bookHeader } = useTypedSelector((state) => state.PariState);

  interface BookCoverProps {
    image: string;
    header: string;
  }

  const booksArray: BookCoverProps[] = [
    { image: c21, header: 'C++ in 21 days' },
    { image: algor, header: 'Alghoritms design' },
    { image: chinese, header: 'harry potter3' },
    { image: gypnos, header: 'harry potter4' },
    { image: HP, header: 'harry potter5' },
    { image: kung, header: 'harry potter6' },
    { image: kant, header: 'harry potter7' },
    { image: landau, header: 'harry potter8' },
    { image: think, header: 'harry potter9' },
  ];

  return (
    <div className={styles.container}>
      <button onClick={() => navigate(-1)}>back</button>
      <h1>choose a book</h1>
      <h4>{bookHeader}</h4>
      <ul>
        {booksArray.map((item, index) => (
          <BookCover image={item.image} header={item.header} key={index} />
        ))}
      </ul>
      <button onClick={() => navigate('/second')}>next</button>
    </div>
  );
}

export default First;
