import React from 'react';
import { useTypedSelector } from '../../hooks/useTypedSelector';
import styles from './Book.module.css';
import { Routes, Route, useLocation } from 'react-router-dom';

function Book() {
  const { bookHeader, bookCover, countHours, countDays, money } = useTypedSelector(
    (state) => state.PariState,
  );

  const loc = useLocation();
  console.log(loc);

  return (
    <div className={styles.container}>
      <img src={bookCover} />
      <div className={styles.aboutBook}>
        <p>{bookHeader}</p>
        {countHours ? <p> will read: {countHours} hours</p> : ''}
        {countDays ? <p>Days need: {countDays}</p> : ''}
        {Number(money) > 0 ? <p>bet: {money} near</p> : <p>bet: 0 near</p>}
        {}
      </div>
    </div>
  );
}

export default Book;
