import { createSlice, PayloadAction } from '@reduxjs/toolkit';
import chineseHP from './chineseHP.jpeg';

interface PariStateType {
  bookHeader: string;
  bookCover: string;
}

interface BookCoverProps {
  image: string;
  header: string;
}
const booksArray: BookCoverProps[] = [
  { image: chineseHP, header: 'harry potter' },
  { image: chineseHP, header: 'harry potter' },
  { image: chineseHP, header: 'harry potter' },
  { image: chineseHP, header: 'harry potter' },
  { image: chineseHP, header: 'harry potter' },
  { image: chineseHP, header: 'harry potter' },
  { image: chineseHP, header: 'harry potter' },
  { image: chineseHP, header: 'harry potter' },
  { image: chineseHP, header: 'harry potter' },
];

const initialState = {
  booksArray: booksArray,
  bookHeader: '',
  bookCover: '',
};
const PariState = createSlice({
  name: 'PariState',
  initialState,
  reducers: {
    setBookHeader(state, action: PayloadAction<string>) {
      state.bookHeader = action.payload;
    },
  },
});

export default PariState.reducer;
export const { setBookHeader } = PariState.actions;
